-- suppression des tables existantes
DROP TABLE p03_RecompenserLaureatCarriere;
DROP TABLE p03_RecompenserLaureatOeuvre;
DROP TABLE p03_Ecrire;
DROP TABLE p03_Livre;
DROP TABLE p03_PrixCarriere;
DROP TABLE p03_PrixOeuvre;
DROP TABLE p03_Laureat;

-- suppression des séquences existantes
DROP sequence seq_PrixCarriere;
DROP sequence seq_PrixOeuvre;
DROP sequence seq_Livre;
DROP sequence seq_Laureat;

-- création d'une séquence pour la table p03_Laureat
CREATE sequence seq_Laureat ;

-- Création de la table p03_Laureat
CREATE TABLE p03_Laureat (
    L_id INT DEFAULT seq_Laureat.nextval,
    L_nom VARCHAR(91) NOT NULL,
    L_prenom VARCHAR(90),
    L_sexe VARCHAR(15) CHECK (L_sexe IN ('Masculin', 'Féminin')),
    L_pays VARCHAR(60),
    PRIMARY KEY (L_id)
);

-- création d'une séquence pour la table p03_Livre
CREATE sequence seq_Livre ;

-- création de la table p03_Livre
CREATE TABLE p03_Livre (
    Liv_id INT DEFAULT seq_Livre.nextval,
    Liv_titre VARCHAR(90) NOT NULL,
    Liv_editeur VARCHAR(90) NOT NULL,
    Liv_anneeEdition INT NOT NULL,
    Liv_nbPages INT,
    PRIMARY KEY (Liv_id)
);

-- création d'une séquence pour la table p03_PrixOeuvre
CREATE sequence seq_PrixOeuvre ;

-- Création de la table p03_PrixOeuvre ( pour les prix récompensant une oeuvre )
CREATE TABLE p03_PrixOeuvre (
    P_Oeu_id INT DEFAULT seq_PrixOeuvre.nextval,
    P_Oeu_nom VARCHAR(90) NOT NULL,
    P_Oeu_anneeDeSortie INT NOT NULL,
    PRIMARY KEY (P_Oeu_id)
);

-- création d'une séquence pour la table p03_PrixCarriere
CREATE sequence seq_PrixCarriere START WITH 1 INCREMENT BY 1;

-- Création de la table p03_Carriere ( pour les prix récompensant un écrivain pour l'ensemble de son oeuvre )
CREATE TABLE p03_PrixCarriere (
    P_C_id INT DEFAULT seq_PrixCarriere.nextval,
    P_C_nom VARCHAR(90) NOT NULL,
    P_C_anneeDeSortie INT NOT NULL,
    PRIMARY KEY (P_C_id)
);

-- Création de la table associative p03_RecompenserLaureatOeuvre ( pour associer chaque prix récompensant une oeuvre à son lauréat )
CREATE TABLE p03_RecompenserLaureatOeuvre (
    L_id INT NOT NULL,
    P_Oeu_id INT NOT NULL,
    dateRO DATE,
    PRIMARY KEY (L_id, P_Oeu_id),
    FOREIGN KEY (L_id) REFERENCES p03_Laureat(L_id),
    FOREIGN KEY (P_Oeu_id) REFERENCES p03_PrixOeuvre(P_Oeu_id)
);

-- Création de la table associative p03_RecompenserLaureatCarriere ( pour associer chaque prix récompensant une carrière d'un écrivain à son lauréat )
CREATE TABLE p03_RecompenserLaureatCarriere (
    L_id INT NOT NULL,
    P_C_id INT NOT NULL,
    dateRC DATE,
    PRIMARY KEY (L_id, P_C_id),
    FOREIGN KEY (L_id) REFERENCES p03_Laureat(L_id),
    FOREIGN KEY (P_C_id) REFERENCES p03_PrixCarriere(P_C_id)
);

-- Création de la table associative p03_Ecrire ( pour associer chaque livre à son prix )
CREATE TABLE p03_Ecrire (
    Liv_id INT NOT NULL,
    L_id INT NOT NULL,
    PRIMARY KEY (Liv_id, L_id),
    FOREIGN KEY (Liv_id) REFERENCES p03_Livre(Liv_id),
    FOREIGN KEY (L_id) REFERENCES p03_Laureat(L_id)
);

